mapper.select:
SELECT p.p_code, c.* 
FROM tbl_post p, tbl_order o, tbl_cart c 
WHERE p.p_code = #{p_code} AND p.p_code = o.p_code AND o.u_code = c.u_code;

select *
from tbl_cart
where u_code = #{u_code} and m_name = #{m_name}

mapper.update:
update tbl_cart
set amount = #{amount}
where u_code = #{u_code} and m_name = #{m_name}
