mapper.update:
update tbl_store
set s_name = #{s_name}, s_location = #{s_location}, s_admin = #{s_admin}, s_tel = #{s_tel}
where s_code = #{s_code}

update tbl_store
set s_status = not(s_status)
where s_code = #{s_code}

update tbl_store
set s_waiting = (
	select count(*)
	from tbl_post
	where s_code = #{s_code}
)
where s_code = #{s_code}

update tbl_store
set s_waiting = (
	select s_waiting
	from (
		select s_waiting
		from tbl_store
		where s_code = #{s_code}
	) as s_waiting
) + #{count}
where s_code = #{s_code};
